import React, { useState, useEffect } from 'react';
import './Gallery.css';

const Gallery = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const slides = [
    { id: 1, src: '../assets/slide1.png', alt: 'Slide 1' },
    { id: 2, src: '../assets/slide1.png', alt: 'Slide 2' },
    { id: 3, src: '../assets/slide1.png', alt: 'Slide 3' },
    { id: 4, src: '../assets/slide1.png', alt: 'Slide 4' },
    { id: 5, src: '../assets/slide1.png', alt: 'Slide 5' },
    { id: 6, src: '../assets/slide1.png', alt: 'Slide 6' }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev === slides.length - 1 ? 0 : prev + 1));
    }, 3000);
    return () => clearInterval(interval);
  }, [slides.length]);

  return (
    <section className="gallery-section">
      <div className="slideshow-container">
        <div 
          className="slideshow-wrapper"
          style={{ transform: `translateX(-${currentSlide * 100}%)` }}
        >
          {slides.map((slide) => (
            <div key={slide.id} className="slide">
              <img src={slide.src} alt={slide.alt} />
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Gallery;
